import React, { useState, useEffect } from 'react';
import ProjectList from './components/ProjectList';
import MilestoneBoard from './components/MilestoneBoard';
import Dashboard from './components/Dashboard';
import './App.css';

function App() {
  const [currentView, setCurrentView] = useState('dashboard');
  const [projects, setProjects] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [selectedProject, setSelectedProject] = useState(null);

  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    try {
      const response = await fetch('http://localhost:8080/api/projects');
      if (response.ok) {
        const data = await response.json();
        setProjects(data);
      }
    } catch (error) {
      console.error('Error fetching projects:', error);
    }
  };



  const addProject = async (project) => {
    try {
      console.log('Creating project:', project);
      const response = await fetch('http://localhost:8080/api/projects', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          name: project.name,
          description: project.description,
          startDate: project.startDate || null,
          endDate: project.endDate || null,
          status: 'NEW'
        }),
      });

      if (response.ok) {
        const newProject = await response.json();
        console.log('Project created:', newProject);
        // Add the new project directly to avoid fetch issues
        setProjects(prev => [...prev, newProject]);
      } else {
        console.error('Failed to create project:', response.status);
      }
    } catch (error) {
      console.error('Error creating project:', error);
    }
  };

  const addMilestone = async (projectId, milestone) => {
    try {
      const response = await fetch(`http://localhost:8080/api/projects/${projectId}/milestones`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          title: milestone.title,
          description: milestone.description,
          dueDate: milestone.dueDate,
          completed: false
        }),
      });
      if (response.ok) {
        await fetchProjects();
        const updatedProjects = await fetch('http://localhost:8080/api/projects').then(r => r.json());
        const updatedProject = updatedProjects.find(p => p.id === projectId);
        setSelectedProject(updatedProject);
      }
    } catch (error) {
      console.error('Error creating milestone:', error);
    }
  };

  const updateMilestone = async (milestoneId, updates) => {
    try {
      const response = await fetch(`http://localhost:8080/api/milestones/${milestoneId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updates),
      });
      if (response.ok) {
        await fetchProjects();
        const updatedProjects = await fetch('http://localhost:8080/api/projects').then(r => r.json());
        const updatedProject = updatedProjects.find(p => p.id === selectedProject?.id);
        setSelectedProject(updatedProject);
      }
    } catch (error) {
      console.error('Error updating milestone:', error);
    }
  };

  return (
    <div className="app">
      <nav className="navbar">
        <h1>Project Management Dashboard</h1>
        <div className="nav-buttons">
          <button 
            className={currentView === 'dashboard' ? 'active' : ''}
            onClick={() => setCurrentView('dashboard')}
          >
            Dashboard
          </button>
          <button 
            className={currentView === 'projects' ? 'active' : ''}
            onClick={() => setCurrentView('projects')}
          >
            Projects
          </button>
          <button 
            className={currentView === 'milestones' ? 'active' : ''}
            onClick={() => setCurrentView('milestones')}
          >
            Milestones
          </button>
        </div>
      </nav>

      <main className="main-content">
        {currentView === 'dashboard' && (
          <Dashboard 
            projects={projects} 
            onNavigate={(view) => setCurrentView(view)}
          />
        )}
        {currentView === 'projects' && (
          <ProjectList 
            projects={projects} 
            onAddProject={addProject}
            onSelectProject={(project) => {
              setSelectedProject(project);
              setCurrentView('milestones');
            }}
          />
        )}
        {currentView === 'milestones' && (
          <MilestoneBoard 
            selectedProject={selectedProject}
            onAddMilestone={addMilestone}
            onUpdateMilestone={updateMilestone}
          />
        )}
      </main>
    </div>
  );
}

export default App;
