import React, { useState } from 'react';

function MilestoneBoard({ selectedProject, onAddMilestone, onUpdateMilestone }) {
  const [showForm, setShowForm] = useState(false);
  const [selectedMilestone, setSelectedMilestone] = useState(null);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    dueDate: ''
  });

  const milestones = selectedProject?.milestones || [];
  const pendingMilestones = milestones.filter(m => !m.completed);
  const completedMilestones = milestones.filter(m => m.completed);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (formData.title.trim() && selectedProject) {
      onAddMilestone(selectedProject.id, formData);
      setFormData({ title: '', description: '', dueDate: '' });
      setShowForm(false);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const toggleMilestone = (milestone) => {
    onUpdateMilestone(milestone.id, { 
      ...milestone, 
      completed: !milestone.completed 
    });
  };

  const handleDragStart = (e, milestone) => {
    e.dataTransfer.setData('text/plain', JSON.stringify(milestone));
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleDrop = (e, targetCompleted) => {
    e.preventDefault();
    const milestone = JSON.parse(e.dataTransfer.getData('text/plain'));
    if (milestone.completed !== targetCompleted) {
      toggleMilestone(milestone);
    }
  };

  const MilestoneItem = ({ milestone }) => (
    <div 
      className="task-item"
      draggable
      onDragStart={(e) => handleDragStart(e, milestone)}
      onClick={() => setSelectedMilestone(milestone)}
      style={{ cursor: 'grab' }}
    >
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
        <h4>{milestone.title}</h4>
        <input
          type="checkbox"
          checked={milestone.completed}
          onChange={(e) => {
            e.stopPropagation();
            toggleMilestone(milestone);
          }}
          style={{ marginLeft: '1rem' }}
        />
      </div>
      <p style={{ fontSize: '0.9rem', color: '#666', margin: '0.5rem 0' }}>
        {milestone.description}
      </p>
      {milestone.dueDate && (
        <div style={{ fontSize: '0.8rem', color: '#666' }}>
          Due: {new Date(milestone.dueDate).toLocaleDateString()}
        </div>
      )}
    </div>
  );

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem' }}>
        <h2>
          Milestones {selectedProject && `- ${selectedProject.name}`}
        </h2>
        <button className="btn btn-success" onClick={() => setShowForm(!showForm)}>
          {showForm ? 'Cancel' : 'New Milestone'}
        </button>
      </div>

      {showForm && (
        <div className="card">
          <h3>Create New Milestone</h3>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Milestone Title</label>
              <input
                type="text"
                name="title"
                value={formData.title}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <label>Description</label>
              <textarea
                name="description"
                value={formData.description}
                onChange={handleChange}
              />
            </div>
            <div className="form-group">
              <label>Due Date</label>
              <input
                type="date"
                name="dueDate"
                value={formData.dueDate}
                onChange={handleChange}
              />
            </div>
            <button type="submit" className="btn btn-success">Create Milestone</button>
          </form>
        </div>
      )}

      <div className="grid grid-2">
        <div 
          className="task-column"
          onDragOver={handleDragOver}
          onDrop={(e) => handleDrop(e, false)}
          style={{ minHeight: '200px', border: '2px dashed transparent' }}
          onDragEnter={(e) => e.currentTarget.style.border = '2px dashed #007bff'}
          onDragLeave={(e) => e.currentTarget.style.border = '2px dashed transparent'}
        >
          <h3>Pending ({pendingMilestones.length})</h3>
          {pendingMilestones.map(milestone => (
            <MilestoneItem key={milestone.id} milestone={milestone} />
          ))}
        </div>

        <div 
          className="task-column"
          onDragOver={handleDragOver}
          onDrop={(e) => handleDrop(e, true)}
          style={{ minHeight: '200px', border: '2px dashed transparent' }}
          onDragEnter={(e) => e.currentTarget.style.border = '2px dashed #28a745'}
          onDragLeave={(e) => e.currentTarget.style.border = '2px dashed transparent'}
        >
          <h3>Completed ({completedMilestones.length})</h3>
          {completedMilestones.map(milestone => (
            <MilestoneItem key={milestone.id} milestone={milestone} />
          ))}
        </div>
      </div>

      {selectedMilestone && (
        <div style={{ 
          position: 'fixed', 
          top: 0, 
          left: 0, 
          right: 0, 
          bottom: 0, 
          background: 'rgba(0,0,0,0.5)', 
          display: 'flex', 
          alignItems: 'center', 
          justifyContent: 'center',
          zIndex: 1000
        }}>
          <div className="card" style={{ width: '500px', maxHeight: '80vh', overflow: 'auto' }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1rem' }}>
              <h3>{selectedMilestone.title}</h3>
              <button onClick={() => setSelectedMilestone(null)} style={{ background: 'none', border: 'none', fontSize: '1.5rem', cursor: 'pointer' }}>Ã—</button>
            </div>
            <p style={{ marginBottom: '1rem' }}>{selectedMilestone.description}</p>
            <div style={{ marginBottom: '1rem' }}>
              <strong>Status:</strong>
              <span className={`status-badge ${selectedMilestone.completed ? 'status-completed' : 'status-todo'}`} style={{ marginLeft: '0.5rem' }}>
                {selectedMilestone.completed ? 'Completed' : 'Pending'}
              </span>
            </div>
            {selectedMilestone.dueDate && (
              <div style={{ marginBottom: '1rem' }}>
                <strong>Due Date:</strong> {new Date(selectedMilestone.dueDate).toLocaleDateString()}
              </div>
            )}
            <button 
              className={`btn ${selectedMilestone.completed ? 'btn-danger' : 'btn-success'}`}
              onClick={() => {
                toggleMilestone(selectedMilestone);
                setSelectedMilestone(null);
              }}
            >
              Mark as {selectedMilestone.completed ? 'Pending' : 'Completed'}
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default MilestoneBoard;
