import React, { useState } from 'react';

function TaskBoard({ tasks, projects, selectedProject, onAddTask, onUpdateTask }) {
  const [showForm, setShowForm] = useState(false);
  const [selectedTask, setSelectedTask] = useState(null);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    projectId: selectedProject?.id || '',
    assignee: '',
    priority: 'Medium',
    dueDate: ''
  });

  const filteredTasks = selectedProject 
    ? tasks.filter(task => task.projectId === selectedProject.id)
    : tasks;

  const tasksByStatus = {
    'To Do': filteredTasks.filter(task => task.status === 'To Do'),
    'In Progress': filteredTasks.filter(task => task.status === 'In Progress'),
    'Completed': filteredTasks.filter(task => task.status === 'Completed')
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (formData.title.trim()) {
      onAddTask(formData);
      setFormData({ title: '', description: '', projectId: selectedProject?.id || '', assignee: '', priority: 'Medium', dueDate: '' });
      setShowForm(false);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleStatusChange = (taskId, newStatus) => {
    onUpdateTask(taskId, { status: newStatus });
  };

  const handleTimeLog = (taskId, hours) => {
    const task = tasks.find(t => t.id === taskId);
    onUpdateTask(taskId, { timeLogged: (task.timeLogged || 0) + hours });
  };

  const TaskItem = ({ task }) => (
    <div 
      className={`task-item priority-${task.priority.toLowerCase()}`}
      onClick={() => setSelectedTask(task)}
    >
      <h4>{task.title}</h4>
      <p style={{ fontSize: '0.9rem', color: '#666', margin: '0.5rem 0' }}>{task.description}</p>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', fontSize: '0.8rem' }}>
        <span className={`status-badge priority-${task.priority.toLowerCase()}`}>{task.priority}</span>
        <span>{task.assignee}</span>
      </div>
      {task.timeLogged > 0 && (
        <div style={{ marginTop: '0.5rem', fontSize: '0.8rem', color: '#666' }}>
          Time logged: {task.timeLogged}h
        </div>
      )}
    </div>
  );

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem' }}>
        <h2>
          Tasks {selectedProject && `- ${selectedProject.name}`}
        </h2>
        <button className="btn btn-success" onClick={() => setShowForm(!showForm)}>
          {showForm ? 'Cancel' : 'New Task'}
        </button>
      </div>

      {showForm && (
        <div className="card">
          <h3>Create New Task</h3>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Task Title</label>
              <input
                type="text"
                name="title"
                value={formData.title}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <label>Description</label>
              <textarea
                name="description"
                value={formData.description}
                onChange={handleChange}
              />
            </div>
            <div className="grid grid-3">
              <div className="form-group">
                <label>Project</label>
                <select name="projectId" value={formData.projectId} onChange={handleChange}>
                  <option value="">Select Project</option>
                  {projects.map(project => (
                    <option key={project.id} value={project.id}>{project.name}</option>
                  ))}
                </select>
              </div>
              <div className="form-group">
                <label>Priority</label>
                <select name="priority" value={formData.priority} onChange={handleChange}>
                  <option value="Low">Low</option>
                  <option value="Medium">Medium</option>
                  <option value="High">High</option>
                </select>
              </div>
              <div className="form-group">
                <label>Due Date</label>
                <input
                  type="date"
                  name="dueDate"
                  value={formData.dueDate}
                  onChange={handleChange}
                />
              </div>
            </div>
            <div className="form-group">
              <label>Assignee</label>
              <input
                type="text"
                name="assignee"
                value={formData.assignee}
                onChange={handleChange}
                placeholder="Team member name"
              />
            </div>
            <button type="submit" className="btn btn-success">Create Task</button>
          </form>
        </div>
      )}

      <div className="task-board">
        {Object.entries(tasksByStatus).map(([status, statusTasks]) => (
          <div key={status} className="task-column">
            <h3>{status} ({statusTasks.length})</h3>
            {statusTasks.map(task => (
              <TaskItem key={task.id} task={task} />
            ))}
          </div>
        ))}
      </div>

      {selectedTask && (
        <div style={{ 
          position: 'fixed', 
          top: 0, 
          left: 0, 
          right: 0, 
          bottom: 0, 
          background: 'rgba(0,0,0,0.5)', 
          display: 'flex', 
          alignItems: 'center', 
          justifyContent: 'center',
          zIndex: 1000
        }}>
          <div className="card" style={{ width: '500px', maxHeight: '80vh', overflow: 'auto' }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1rem' }}>
              <h3>{selectedTask.title}</h3>
              <button onClick={() => setSelectedTask(null)} style={{ background: 'none', border: 'none', fontSize: '1.5rem', cursor: 'pointer' }}>×</button>
            </div>
            <p style={{ marginBottom: '1rem' }}>{selectedTask.description}</p>
            <div style={{ marginBottom: '1rem' }}>
              <strong>Status:</strong>
              <select 
                value={selectedTask.status} 
                onChange={(e) => handleStatusChange(selectedTask.id, e.target.value)}
                style={{ marginLeft: '0.5rem', padding: '0.25rem' }}
              >
                <option value="To Do">To Do</option>
                <option value="In Progress">In Progress</option>
                <option value="Completed">Completed</option>
              </select>
            </div>
            <div style={{ marginBottom: '1rem' }}>
              <strong>Priority:</strong> <span className={`status-badge priority-${selectedTask.priority.toLowerCase()}`}>{selectedTask.priority}</span>
            </div>
            <div style={{ marginBottom: '1rem' }}>
              <strong>Assignee:</strong> {selectedTask.assignee || 'Unassigned'}
            </div>
            <div style={{ marginBottom: '1rem' }}>
              <strong>Time Logged:</strong> {selectedTask.timeLogged || 0} hours
              <div style={{ marginTop: '0.5rem' }}>
                <button className="btn" onClick={() => handleTimeLog(selectedTask.id, 1)} style={{ marginRight: '0.5rem' }}>+1h</button>
                <button className="btn" onClick={() => handleTimeLog(selectedTask.id, 2)} style={{ marginRight: '0.5rem' }}>+2h</button>
                <button className="btn" onClick={() => handleTimeLog(selectedTask.id, 4)}>+4h</button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default TaskBoard;
