import React, { useState, useEffect } from 'react';
import ProjectList from './components/ProjectList';
import TaskBoard from './components/TaskBoard';
import Dashboard from './components/Dashboard';
import './App.css';

function App() {
  const [currentView, setCurrentView] = useState('dashboard');
  const [projects, setProjects] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [selectedProject, setSelectedProject] = useState(null);

  useEffect(() => {
    // Load initial data
    const savedProjects = JSON.parse(localStorage.getItem('projects') || '[]');
    const savedTasks = JSON.parse(localStorage.getItem('tasks') || '[]');
    setProjects(savedProjects);
    setTasks(savedTasks);
  }, []);

  const saveData = (newProjects, newTasks) => {
    localStorage.setItem('projects', JSON.stringify(newProjects));
    localStorage.setItem('tasks', JSON.stringify(newTasks));
  };

  const addProject = (project) => {
    const newProject = { ...project, id: Date.now(), status: 'On Track' };
    const newProjects = [...projects, newProject];
    setProjects(newProjects);
    saveData(newProjects, tasks);
  };

  const addTask = (task) => {
    const newTask = { ...task, id: Date.now(), status: 'To Do', timeLogged: 0 };
    const newTasks = [...tasks, newTask];
    setTasks(newTasks);
    saveData(projects, newTasks);
  };

  const updateTask = (taskId, updates) => {
    const newTasks = tasks.map(task => 
      task.id === taskId ? { ...task, ...updates } : task
    );
    setTasks(newTasks);
    saveData(projects, newTasks);
  };

  return (
    <div className="app">
      <nav className="navbar">
        <h1>Project Management Dashboard</h1>
        <div className="nav-buttons">
          <button 
            className={currentView === 'dashboard' ? 'active' : ''}
            onClick={() => setCurrentView('dashboard')}
          >
            Dashboard
          </button>
          <button 
            className={currentView === 'projects' ? 'active' : ''}
            onClick={() => setCurrentView('projects')}
          >
            Projects
          </button>
          <button 
            className={currentView === 'tasks' ? 'active' : ''}
            onClick={() => setCurrentView('tasks')}
          >
            Tasks
          </button>
        </div>
      </nav>

      <main className="main-content">
        {currentView === 'dashboard' && (
          <Dashboard projects={projects} tasks={tasks} />
        )}
        {currentView === 'projects' && (
          <ProjectList 
            projects={projects} 
            onAddProject={addProject}
            onSelectProject={(project) => {
              setSelectedProject(project);
              setCurrentView('tasks');
            }}
          />
        )}
        {currentView === 'tasks' && (
          <TaskBoard 
            tasks={tasks}
            projects={projects}
            selectedProject={selectedProject}
            onAddTask={addTask}
            onUpdateTask={updateTask}
          />
        )}
      </main>
    </div>
  );
}

export default App;
