import React, { useState, useEffect } from 'react';

function Notifications({ userId = 'demo-user' }) {
  const [notifications, setNotifications] = useState([]);
  const [showNotifications, setShowNotifications] = useState(false);

  useEffect(() => {
    fetchNotifications();
  }, [userId]);

  const fetchNotifications = async () => {
    try {
      const response = await fetch(`http://localhost:8080/api/notifications?userId=${userId}`);
      if (response.ok) {
        const data = await response.json();
        setNotifications(data);
      }
    } catch (error) {
      console.error('Error fetching notifications:', error);
    }
  };

  const markAsRead = async (notificationId) => {
    try {
      const response = await fetch(`http://localhost:8080/api/notifications/${notificationId}/read`, {
        method: 'PUT'
      });
      if (response.ok) {
        fetchNotifications();
      }
    } catch (error) {
      console.error('Error marking notification as read:', error);
    }
  };

  const unreadCount = notifications.filter(n => !n.isRead).length;

  return (
    <div style={{ position: 'relative', display: 'inline-block' }}>
      <button 
        onClick={() => setShowNotifications(!showNotifications)}
        style={{ 
          position: 'relative',
          background: '#007bff',
          color: 'white',
          border: 'none',
          padding: '0.5rem 1rem',
          borderRadius: '4px',
          cursor: 'pointer'
        }}
      >
        ðŸ”” Notifications
        {unreadCount > 0 && (
          <span style={{
            position: 'absolute',
            top: '-5px',
            right: '-5px',
            background: 'red',
            color: 'white',
            borderRadius: '50%',
            width: '20px',
            height: '20px',
            fontSize: '12px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center'
          }}>
            {unreadCount}
          </span>
        )}
      </button>

      {showNotifications && (
        <>
          <div style={{
            position: 'fixed',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: 'rgba(0,0,0,0.3)',
            zIndex: 99998
          }} onClick={() => setShowNotifications(false)} />
          <div style={{
            position: 'fixed',
            top: '70px',
            right: '10px',
            width: '380px',
            maxHeight: '70vh',
            overflow: 'auto',
            background: 'white',
            border: '1px solid #ddd',
            borderRadius: '8px',
            boxShadow: '0 4px 20px rgba(0,0,0,0.15)',
            zIndex: 99999
          }}>
          <div style={{ padding: '1rem', borderBottom: '1px solid #eee' }}>
            <h4 style={{ margin: 0 }}>Notifications</h4>
          </div>

          {notifications.length === 0 ? (
            <div style={{ padding: '1rem', color: '#666' }}>No notifications</div>
          ) : (
            notifications.map(notification => (
              <div 
                key={notification.id}
                style={{
                  padding: '0.75rem',
                  borderBottom: '1px solid #eee',
                  backgroundColor: notification.isRead ? 'white' : '#f0f8ff',
                  cursor: 'pointer'
                }}
                onClick={() => !notification.isRead && markAsRead(notification.id)}
              >
                <div style={{ fontSize: '0.9rem', marginBottom: '0.25rem', lineHeight: '1.4', wordWrap: 'break-word' }}>
                  {notification.message}
                </div>
                <div style={{ fontSize: '0.7rem', color: '#666' }}>
                  {new Date(notification.createdAt).toLocaleString()}
                  {!notification.isRead && (
                    <span style={{ color: '#007bff', marginLeft: '0.5rem' }}>â€¢ New</span>
                  )}
                </div>
              </div>
            ))
          )}
          </div>
        </>
      )}
    </div>
  );
}

export default Notifications;
