import React, { useState } from 'react';

function TaskBoard({ selectedProject, onAddTask, onUpdateTask, onDeleteTask, allTasks = [] }) {
  const [showForm, setShowForm] = useState(false);
  const [selectedTask, setSelectedTask] = useState(null);
  const [viewAllTasks, setViewAllTasks] = useState(false);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    assignedToUserId: '',
    priority: 'MEDIUM',
    status: 'TODO',
    dueDate: ''
  });

  const tasks = viewAllTasks ? allTasks : (selectedProject?.tasks || []);
  const todoTasks = tasks.filter(t => t.status === 'TODO');
  const inProgressTasks = tasks.filter(t => t.status === 'IN_PROGRESS');
  const doneTasks = tasks.filter(t => t.status === 'DONE');

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted:', formData, 'Project:', selectedProject);
    if (formData.title.trim() && selectedProject) {
      onAddTask(selectedProject.id, formData);
      setFormData({ title: '', description: '', assignedToUserId: '', priority: 'MEDIUM', status: 'TODO', dueDate: '' });
      setShowForm(false);
    } else {
      console.log('Form validation failed - title:', formData.title, 'project:', selectedProject);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const updateTaskStatus = (task, newStatus) => {
    onUpdateTask(task.id, { ...task, status: newStatus });
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'HIGH': return '#ff4757';
      case 'MEDIUM': return '#ffa502';
      case 'LOW': return '#2ed573';
      default: return '#747d8c';
    }
  };

  const handleDragStart = (e, task) => {
    e.dataTransfer.setData('text/plain', JSON.stringify(task));
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleDrop = (e, newStatus) => {
    e.preventDefault();
    const task = JSON.parse(e.dataTransfer.getData('text/plain'));
    if (task.status !== newStatus) {
      updateTaskStatus(task, newStatus);
    }
  };

  const TaskItem = ({ task }) => (
    <div 
      className="task-item" 
      draggable
      onDragStart={(e) => handleDragStart(e, task)}
      onClick={() => setSelectedTask(task)}
      style={{ cursor: 'grab' }}
    >
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
        <h4>{task.title}</h4>
        <span style={{ 
          backgroundColor: getPriorityColor(task.priority), 
          color: 'white', 
          padding: '2px 6px', 
          borderRadius: '3px', 
          fontSize: '0.7rem' 
        }}>
          {task.priority}
        </span>
      </div>
      <p style={{ fontSize: '0.9rem', color: '#666', margin: '0.5rem 0' }}>
        {task.description}
      </p>
      {task.assignedToUserId && (
        <div style={{ fontSize: '0.8rem', color: '#666' }}>
          Assigned to: {task.assignedToUserId}
        </div>
      )}
      {task.dueDate && (
        <div style={{ fontSize: '0.8rem', color: '#666' }}>
          Due: {new Date(task.dueDate).toLocaleDateString()}
        </div>
      )}
    </div>
  );

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem' }}>
        <div>
          <h2>Tasks {!viewAllTasks && selectedProject && `- ${selectedProject.name}`}</h2>
          <div style={{ marginTop: '0.5rem' }}>
            <button 
              onClick={() => setViewAllTasks(!viewAllTasks)}
              style={{ background: 'none', border: 'none', color: '#007bff', cursor: 'pointer', fontSize: '0.9rem' }}
            >
              {viewAllTasks ? 'Show Project Tasks' : 'Show All Tasks'}
            </button>
          </div>
        </div>
        <button 
          className="btn btn-success" 
          onClick={() => setShowForm(!showForm)}
          disabled={!selectedProject && !viewAllTasks}
        >
          {showForm ? 'Cancel' : 'New Task'}
        </button>
      </div>

      {showForm && (
        <div className="card">
          <h3>Create New Task</h3>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Task Title</label>
              <input
                type="text"
                name="title"
                value={formData.title}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <label>Description</label>
              <textarea
                name="description"
                value={formData.description}
                onChange={handleChange}
              />
            </div>
            <div className="grid grid-2">
              <div className="form-group">
                <label>Assign To</label>
                <input
                  type="text"
                  name="assignedToUserId"
                  value={formData.assignedToUserId}
                  onChange={handleChange}
                  placeholder="User ID"
                />
              </div>
              <div className="form-group">
                <label>Priority</label>
                <select name="priority" value={formData.priority} onChange={handleChange}>
                  <option value="LOW">Low</option>
                  <option value="MEDIUM">Medium</option>
                  <option value="HIGH">High</option>
                </select>
              </div>
            </div>
            <div className="form-group">
              <label>Due Date</label>
              <input
                type="date"
                name="dueDate"
                value={formData.dueDate}
                onChange={handleChange}
              />
            </div>
            <button type="submit" className="btn btn-success">Create Task</button>
          </form>
        </div>
      )}

      <div className="grid grid-3">
        <div 
          className="task-column"
          onDragOver={handleDragOver}
          onDrop={(e) => handleDrop(e, 'TODO')}
        >
          <h3>To Do ({todoTasks.length})</h3>
          {todoTasks.map(task => (
            <TaskItem key={task.id} task={task} />
          ))}
        </div>

        <div 
          className="task-column"
          onDragOver={handleDragOver}
          onDrop={(e) => handleDrop(e, 'IN_PROGRESS')}
        >
          <h3>In Progress ({inProgressTasks.length})</h3>
          {inProgressTasks.map(task => (
            <TaskItem key={task.id} task={task} />
          ))}
        </div>

        <div 
          className="task-column"
          onDragOver={handleDragOver}
          onDrop={(e) => handleDrop(e, 'DONE')}
        >
          <h3>Done ({doneTasks.length})</h3>
          {doneTasks.map(task => (
            <TaskItem key={task.id} task={task} />
          ))}
        </div>
      </div>

      {selectedTask && (
        <div style={{ 
          position: 'fixed', 
          top: 0, 
          left: 0, 
          right: 0, 
          bottom: 0, 
          background: 'rgba(0,0,0,0.5)', 
          display: 'flex', 
          alignItems: 'center', 
          justifyContent: 'center',
          zIndex: 1000
        }}>
          <div className="card" style={{ width: '500px', maxHeight: '80vh', overflow: 'auto' }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1rem' }}>
              <h3>{selectedTask.title}</h3>
              <button onClick={() => setSelectedTask(null)} style={{ background: 'none', border: 'none', fontSize: '1.5rem', cursor: 'pointer' }}>×</button>
            </div>
            <p style={{ marginBottom: '1rem' }}>{selectedTask.description}</p>
            <div style={{ marginBottom: '1rem' }}>
              <strong>Priority:</strong>
              <select 
                value={selectedTask.priority} 
                onChange={(e) => setSelectedTask({...selectedTask, priority: e.target.value})}
                style={{ marginLeft: '0.5rem' }}
              >
                <option value="LOW">Low</option>
                <option value="MEDIUM">Medium</option>
                <option value="HIGH">High</option>
              </select>
            </div>
            <div style={{ marginBottom: '1rem' }}>
              <strong>Status:</strong>
              <select 
                value={selectedTask.status} 
                onChange={(e) => updateTaskStatus(selectedTask, e.target.value)}
                style={{ marginLeft: '0.5rem' }}
              >
                <option value="TODO">To Do</option>
                <option value="IN_PROGRESS">In Progress</option>
                <option value="DONE">Done</option>
              </select>
            </div>
            {selectedTask.assignedToUserId && (
              <div style={{ marginBottom: '1rem' }}>
                <strong>Assigned to:</strong> {selectedTask.assignedToUserId}
              </div>
            )}
            {selectedTask.dueDate && (
              <div style={{ marginBottom: '1rem' }}>
                <strong>Due Date:</strong> {new Date(selectedTask.dueDate).toLocaleDateString()}
              </div>
            )}
            <div style={{ display: 'flex', gap: '1rem' }}>
              <button 
                className="btn btn-success"
                onClick={() => {
                  onUpdateTask(selectedTask.id, selectedTask);
                  setSelectedTask(null);
                }}
              >
                Save Changes
              </button>
              <button 
                className="btn btn-danger"
                onClick={() => {
                  onDeleteTask(selectedTask.id);
                  setSelectedTask(null);
                }}
              >
                Delete Task
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default TaskBoard;
